import React, {useState, useEffect} from 'react';
// import CharNavbar from './CharNavbar';
// import Storage from './Storage';
import axios from 'axios';
// import './details.css';
import {Link}  from '@reach/router';

const Dashboard = () => {
  
    const [allTours, setallTours] = useState([])
    // const [jwtStr, setjwtStr]            = useState('');

    useEffect(() => {
        // setjwtStr(Storage.get("token"))
        // console.log(Storage.get("token"))
     
        getTours();
    },[ ]);
    // const getToken = () =>{
    //     return Storage.get("token")
    // };
 
    

 


const getTours  = () => {
 
    console.log("handle fetching data")
    axios.get('https://localhost:44360/api/Tours/')
      .then((res) => {
        console.log(res.data)
        setallTours(res.data)
      })
      .catch((error) => {
        console.error(error)
      })   
}


const deleteTour  = (tourId) => {
    console.log("handle delete")
    axios.delete(`https://localhost:44360/api/Tours/${tourId}/`)
      .then((res) => {
        window.location.reload( );
    
      })
      .catch((error) => {
        console.error(error)
      })   
}
    return (
     
      <div className="container">
      {/* <CharNavbar />  */}

        <table className="table table-striped" >
      <tbody>
        <tr className="bg-dark text-light">
        <th>Tour</th>
        <th>Actions</th>
        </tr>
        {allTours.map((tour,i)=>{
  return(    
<tr >
<td>{tour.place}</td>
<td>
<button className="btn btn-outline-danger btn-sm"  onClick={()=>deleteTour(tour.tourId)} >Cancel </button>
&nbsp;
{/* <Link to={"/" } className="btn btn-sm btn-outline-success">Disply</Link> */}
{/* <Link to={`/AddTour/${tour.tourId}`}      className="btn btn-outline-success btn-sm"   >   </Link> */}

&nbsp;

</td> 
</tr>
  )
})
}    
      </tbody>
    </table>

      </div>


         
     
      );
  };
         {/* <tr>
         <td> {tour.place}</td>
         <td>  

         <button className = "btn btn-outline-danger" onClick={()=>deleteTour(tour.tourId)}> delete </button>

            </td>
         </tr> */}
           
  
    
 


export default Dashboard
