const Storage = (function() {
	let myStorage = window.localStorage;

	function get(name) {
		return myStorage.getItem(name);
	}

	function set(name, value) {
		myStorage.setItem(name, value);
	}

	function clear(name) {
		myStorage.clear();
	}

	return {
		get,
		set,
		clear
	};
})();

export default Storage;
